    def export(self) -> Dict:
        result = dict()
        result["constant_road"] = True
        result["road_width"] = self.road_width
        result["simulator_scene_name"] = self.simulator_scene.get_scene_name()
        if self.simulator_scene.get_scene_name() == GENERATED_TRACK_NAME:
            result["simulator_track_num"] = cast(
                GeneratedTrack, self.simulator_scene
            ).track_num
        return result

    def clone(self) -> "Road":
        return DonkeyConstantRoad(
            simulator_scene=self.simulator_scene, road_width=self.road_width
        )

    def get_concrete_representation(self, to_plot: bool = False) -> List[Tuple4F]:
        if to_plot:
            return [
                (point.x, point.y, point.z, self.road_width)
                for point in self.road_points
            ]
        return [
            (point.x, point.z, point.y, self.road_width) for point in self.road_points
        ]

    def get_inverse_concrete_representation(
        self, to_plot: bool = False
    ) -> List[Tuple4F]:
        if to_plot:
            return [
                (point.x, point.y, point.z, self.road_width)
                for point in reversed(self.road_points)
            ]
        return [
            (point.x, point.z, point.y, self.road_width)
            for point in reversed(self.road_points)
        ]

    def serialize_concrete_representation(self, cr: List[Tuple4F]) -> str:
        # for DONKEY we only need x, y, z
        to_string = []
        for t in cr:
            s = "{},{},{}".format(t[0], t[1], t[2])
            to_string.append(s)
        return "@".join(to_string)

    def get_closest_control_point_index(self, point: Point) -> int:
        return self.get_closest_index(point=point, points=self.control_points)

    def get_closest_road_point_index(self, point: Point) -> int:
        return self.get_closest_index(point=point, points=self.road_points)

    @staticmethod
    def get_closest_index(point: Point, points: List[Point]) -> int:
        assert point.has_z, "Point has no z coordinate"
        min_distance = np.inf
        min_distance_index = -1
        point_2d = Point(point.x, point.z)
        for i in range(len(points)):
            current_point = points[i]
            current_point_2d = Point(current_point.x, current_point.z)
            dist = round(current_point_2d.distance(other=point_2d), 2)
            if dist < min_distance:
                min_distance = dist
                min_distance_index = i
        assert min_distance_index != -1, "Error in setting min_distance_index."
        return min_distance_index
